import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';
import Alert  from 'react-bootstrap/Alert';

function App() {

  const [currentTime, setCurrentTime] = useState(0);

  const [sudoku_grid, setSudokuGrid] = useState([]);

  useEffect(() => {
    fetch('/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    });
  }, []);

  useEffect(() => {
    fetch('/generate_board').then(res => res.json()).then(data => {
      setSudokuGrid(data.board);
    });
  }, []);



  const [show, setShow] = useState(true);

  return (
    <div className="App">
      <header className="App-header">


        <p>The board is {sudoku_grid}</p>
        <Button onClick={() => window.location.reload()}>Test</Button>
      </header>
      <body>
        <Alert show={show} variant="success">
          <Alert.Heading>How's it going?!</Alert.Heading>
          <p>
            Well done Caroline with your project, you're the best!
        </p>
        <p>The current time is {currentTime}.</p>
          <hr />
          <div className="d-flex justify-content-end">
            <Button onClick={() => setShow(false)} variant="outline-success">
              Close me ya'll!
          </Button>
          </div>
        </Alert>

        {!show && <Button onClick={() => setShow(true)} variant="outline-danger">Show Alert</Button>}
      </body>
    </div>
  );
}

export default App;
